const { web3, myAccount } = require("./utils");
const { bytecode } = require("./contractArtifacts");

async function deploy() {
  // send transaction to the network
  let r = await web3.eth
    .sendTransaction({
      from: myAccount.address,
      gas: 800000,
      data: bytecode,
    })
    .on("receipt", console.log);
}

// Run once!
deploy();

